import React, { useEffect, useState } from 'react';

function Projects() {
  const [projects, setProjects] = useState([]);

  useEffect(() => {
    // Faz a chamada para a API e atualiza o estado com os projetos
    fetch('http://127.0.0.1:8000/api/projects/')
      .then((response) => response.json())
      .then((data) => {
        setProjects(data); // Atualiza o estado com os projetos
      })
      .catch((error) => console.error('Erro ao carregar os projetos:', error));
  }, []);

  return (
    <section id="projects" className="projects container">
      <div id="projects-container">
        {projects.length > 0 ? (
          projects.map((project) => (
            <div key={project.id} className="project-card">
              <h3>{project.title}</h3>
              <p>{project.description}</p>
              {project.image && <img src={project.image} alt={project.title} />}
              {/* Bot√£o para ver detalhes */}
              <a href={`/project/${project.id}`} className="btn">
                Ver Detalhes
              </a>
            </div>
          ))
        ) : (
          <p>Carregando projetos...</p>
        )}
      </div>
    </section>
  );
}

export default Projects;
