import React, { useState, useEffect } from 'react';
import ProjectCard from './ProjectCard'; // Importa o componente ProjectCard

const App = () => {
  // Estado para armazenar os projetos
  const [projects, setProjects] = useState([]);

  // Função para buscar os projetos da API
  useEffect(() => {
    fetch('http://127.0.0.1:8000/api/projects/')
      .then(response => response.json()) // Converte a resposta para JSON
      .then(data => setProjects(data)) // Atualiza o estado com os dados dos projetos
      .catch(error => console.error('Erro ao carregar os projetos:', error));
  }, []); // O array vazio [] faz a chamada da API apenas uma vez, quando o componente for montado

  return (
    <div>
      <header className="header">
        <h1>Meu portfólio</h1>
        <nav className="navbar">
          <a href="#about">Sobre</a>
          <a href="#projects">Projetos</a>
          <a href="#technologies">Tecnologias</a>
          <a href="#contact">Contato</a>
        </nav>
      </header>

      <section id="projects" className="projects container">
        {/* Mapeia os projetos e renderiza o componente ProjectCard */}
        {projects.length > 0 ? (
          projects.map(project => (
            <ProjectCard key={project.id} project={project} />
          ))
        ) : (
          <p>Carregando projetos...</p>
        )}
      </section>

      <section id="technologies" className="technologies container">
        <h2>Tecnologias</h2>
        <p>As principais tecnologias que utilizo:</p>
        <ul>
          <li>HTML5</li>
          <li>CSS3</li>
          <li>JavaScript</li>
          <li>React</li>
          <li>Node.js</li>
        </ul>
      </section>

      <section id="contact" className="contact container">
        <h2>Contato</h2>
        <p>Entre em contato comigo através do e-mail: <a href="mailto:ff.s4ntoss@gmail.com">ff.s4ntoss@gmail.com</a></p>
        <p>Ou pelo WhatsApp: <a href="https://wa.me/5561986115281?text=Olá%20Fernando!%20Tenho%20interesse%20nos%20seus%20serviços.">Clique aqui</a></p>
      </section>

      <footer className="footer">
        <p>&copy; 2025 Fernando Fernandes. Todos os direitos reservados.</p>
      </footer>
    </div>
  );
};

export default App;
