import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import './styles.css';

// Componente que faz a requisição à API e exibe os projetos
const ProjectList = () => {
  const [projects, setProjects] = useState([]);

  // Requisição para a API (substitua pela URL real da sua API)
  useEffect(() => {
    fetch('https://sua-api.com/projetos')  // Substitua com a URL da sua API
      .then((response) => response.json())
      .then((data) => setProjects(data))
      .catch((error) => console.error('Erro ao buscar os projetos:', error));
  }, []); // O array vazio [] garante que o efeito seja executado apenas uma vez após o componente ser montado

  return (
    <div className="projects">
      {projects.map((project) => (
        <div key={project.id} className="project-card">
          <h3>{project.title}</h3>
          <p>{project.description}</p>
        </div>
      ))}
    </div>
  );
};

ReactDOM.render(<ProjectList />, document.getElementById('project-list'));
